t.script:
document.addEventListener('DOMContentLoaded', function() {
console.log('Dashboard initialized');

// Initialize Bootstrap tabs
var triggerTabList = [].slice.call(document.querySelectorAll('.header-left
button[data-bs-toggle="tab"]'));
triggerTabList.forEach(function (triggerEl) {
var tabTrigger = new bootstrap.Tab(triggerEl);

triggerEl.addEventListener('click', function (event) {
event.preventDefault();
console.log('Tab clicked:', triggerEl.getAttribute('data-bs-target'));
tabTrigger.show();
});
});

// Store and restore active tab
var activeTab = localStorage.getItem('activeDashboardTab');
if (activeTab) {
console.log('Restoring tab:', activeTab);
var tab = document.querySelector('.header-left button[data-bs-target="' + activeTab
+ '"]');
if (tab) {
var tabInstance = new bootstrap.Tab(tab);
tabInstance.show();
}
}

// Save active tab on change
triggerTabList.forEach(function(tab) {
tab.addEventListener('shown.bs.tab', function (e) {
var target = e.target.getAttribute('data-bs-target');
console.log('Tab shown:', target);
localStorage.setItem('activeDashboardTab', target);
});
});

// Quick action card tab switching
document.querySelectorAll('.action-card[data-bs-toggle="tab"]').forEach(function(card)
{
card.addEventListener('click', function(e) {
e.preventDefault();
var target = this.getAttribute('href');
var tab = document.querySelector('.header-left button[data-bs-target="' + target +
'"]');
if (tab) {
var tabInstance = new bootstrap.Tab(tab);
tabInstance.show();
}
});
});
});
