t.script:
document.addEventListener('DOMContentLoaded', function() {
// Initialize Bootstrap tabs
var triggerTabList = [].slice.call(document.querySelectorAll('#dashboardTabs
button'));
triggerTabList.forEach(function (triggerEl) {
var tabTrigger = new bootstrap.Tab(triggerEl);
triggerEl.addEventListener('click', function (event) {
event.preventDefault();
tabTrigger.show();
});
});

// Store active tab in session storage
var activeTab = localStorage.getItem('activeDashboardTab');
if (activeTab) {
var tab = document.querySelector(activeTab);
if (tab) {
new bootstrap.Tab(tab).show();
}
}

// Save active tab on change
document.getElementById('dashboardTabs').addEventListener('shown.bs.tab', function
(e) {
localStorage.setItem('activeDashboardTab', e.target.getAttribute('data-bs-target'));
});

// Handle sidebar links that should trigger tabs
var sidebarLinks = document.querySelectorAll('.nav-link[data-bs-toggle="tab"]');
sidebarLinks.forEach(function(link) {
link.addEventListener('click', function(e) {
e.preventDefault();
var target = this.getAttribute('data-bs-target');
var tab = document.querySelector('[data-bs-target="' + target + '"]');
if (tab) {
new bootstrap.Tab(tab).show();
}
});
});
});
