t.script:

document.addEventListener('DOMContentLoaded', function() {
    const attachmentInput = document.getElementById('attachment');
    const attachmentDropArea = document.getElementById('attachmentDropArea');
    const browseLink = document.getElementById('browseLink');
    const fileList = document.getElementById('fileList');
    let selectedFiles = [];

    // Click on browse link
    browseLink.addEventListener('click', function(e) {
        e.preventDefault();
        e.stopPropagation(); // Stop bubbling to drop area
        attachmentInput.click();
    });

    // Click on drop area
    attachmentDropArea.addEventListener('click', function(e) {
        // Using standard JS logical operators now thanks to CDATA
        if (e.target !== browseLink && !e.target.closest('.browse-link')) {
            attachmentInput.click();
        }
    });

    // Handle file input change
    attachmentInput.addEventListener('change', function() {
        if (this.files.length > 0) {
            addFiles(Array.from(this.files));
        }
    });

    // Drag and drop functionality
    ['dragenter', 'dragover', 'dragleave', 'drop'].forEach(eventName => {
        attachmentDropArea.addEventListener(eventName, preventDefaults, false);
    });

    function preventDefaults(e) {
        e.preventDefault();
        e.stopPropagation();
    }

    ['dragenter', 'dragover'].forEach(eventName => {
        attachmentDropArea.addEventListener(eventName, highlight, false);
    });

    ['dragleave', 'drop'].forEach(eventName => {
        attachmentDropArea.addEventListener(eventName, unhighlight, false);
    });

    function highlight() {
        attachmentDropArea.classList.add('dragover');
    }

    function unhighlight() {
        attachmentDropArea.classList.remove('dragover');
    }

    // Handle dropped files
    attachmentDropArea.addEventListener('drop', handleDrop, false);

    function handleDrop(e) {
        const dt = e.dataTransfer;
        const files = dt.files;
        addFiles(Array.from(files));
    }

    function addFiles(files) {
        files.forEach(file => {
            if (file.size > 10 * 1024 * 1024) {
                alert(`File "${file.name}" exceeds 10MB limit.`);
                return;
            }
            // Check for duplicates
            if(!selectedFiles.some(f => f.name === file.name && f.size === file.size)){
                selectedFiles.push(file);
            }
        });
        updateFileList();
    }

    function removeFile(index) {
        selectedFiles.splice(index, 1);
        updateFileList();
    }

    function updateFileList() {
        const dt = new DataTransfer();
        selectedFiles.forEach(file => {
            dt.items.add(file);
        });
        attachmentInput.files = dt.files;

        if (selectedFiles.length > 0) {
            attachmentDropArea.classList.add('hide');
            fileList.classList.add('show');

            fileList.innerHTML = '';

            selectedFiles.forEach((file, index) => {
                const fileItem = document.createElement('div');
                fileItem.className = 'file-item';

                let iconClass = 'fas fa-file';
                if (file.type.startsWith('image/')) iconClass = 'fas fa-file-image';
                else if (file.type === 'application/pdf') iconClass = 'fas fa-file-pdf';
                else if (file.type.includes('word') || file.type.includes('document')) iconClass = 'fas fa-file-word';

                fileItem.innerHTML = `
                    <i class="${iconClass} file-icon"></i>
                    <div class="file-name">${file.name} (${formatFileSize(file.size)})</div>
                    <button type="button" class="file-remove" data-index="${index}">
                        <i class="fas fa-times"></i>
                    </button>
                `;
                fileList.appendChild(fileItem);
            });

            document.querySelectorAll('.file-remove').forEach(btn => {
                btn.addEventListener('click', function(e) {
                    const index = parseInt(this.getAttribute('data-index'));
                    removeFile(index);
                });
            });
        } else {
            attachmentDropArea.classList.remove('hide');
            fileList.classList.remove('show');
            fileList.innerHTML = '';
        }
    }

    function formatFileSize(bytes) {
        if (bytes === 0) return '0 Bytes';
        const k = 1024;
        const sizes = ['Bytes', 'KB', 'MB', 'GB'];
        const i = Math.floor(Math.log(bytes) / Math.log(k));
        return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
    }
});
