t.script:
document.addEventListener('DOMContentLoaded', function() {
    // Add ripple effect to button
    const button = document.getElementById('getStartedBtn');

    button.addEventListener('click', function(e) {
        const ripple = document.createElement('span');
        ripple.classList.add('ripple');

        const rect = this.getBoundingClientRect();
        const size = Math.max(rect.width, rect.height);
        const x = e.clientX - rect.left - size / 2;
        const y = e.clientY - rect.top - size / 2;

        ripple.style.width = ripple.style.height = size + 'px';
        ripple.style.left = x + 'px';
        ripple.style.top = y + 'px';

        this.appendChild(ripple);

        setTimeout(() => {
            ripple.remove();
        }, 600);
    });

    // Add loaded class for animations
    setTimeout(() => {
        document.querySelector('.minimal-landing').classList.add('loaded');
    }, 100);

    // Parallax effect on mouse move
    const welcomeBox = document.querySelector('.welcome-box');

    document.addEventListener('mousemove', function(e) {
        const x = (e.clientX / window.innerWidth - 0.5) * 20;
        const y = (e.clientY / window.innerHeight - 0.5) * 20;

        welcomeBox.style.transform = `perspective(1000px) rotateY(${x * 0.1}deg) rotateX(${-y * 0.1}deg)`;
    });

    // Hide scroll indicator on scroll
    const scrollIndicator = document.querySelector('.scroll-indicator');
    if (scrollIndicator) {
        window.addEventListener('scroll', function() {
            if (window.scrollY > 50) {
                scrollIndicator.style.opacity = '0';
            } else {
                scrollIndicator.style.opacity = '0.7';
            }
        });
    }

    // Smooth reveal on page load
    const observer = new IntersectionObserver((entries) => {
        entries.forEach(entry => {
            if (entry.isIntersecting) {
                entry.target.style.opacity = '1';
                entry.target.style.transform = 'translateY(0)';
            }
        });
    }, {
        threshold: 0.1
    });

    // Add subtle float animation to logo on hover
    const logoCircle = document.querySelector('.logo-circle');
    logoCircle.addEventListener('mouseenter', function() {
        this.style.animation = 'logoGlow 3s ease-in-out infinite, logoFloat 2s ease-in-out infinite';
    });

    logoCircle.addEventListener('mouseleave', function() {
        this.style.animation = 'logoGlow 3s ease-in-out infinite';
    });
});

// Additional logo float animation
const style = document.createElement('style');
style.textContent = `
    @keyframes logoFloat {
        0%, 100% {
            transform: translateY(0) scale(1);
        }
        50% {
            transform: translateY(-10px) scale(1.12);
        }
    }
`;
document.head.appendChild(style);
